extension JobStatusTypeUtils on JobStatusType {
  JobStatusType get next {
    final index = (this.index + 1) % JobStatusType.values.length;
    return JobStatusType.values[index];
  }

  JobStatusType get previous {
    final index = (this.index - 1 + JobStatusType.values.length) % JobStatusType.values.length;
    return JobStatusType.values[index];
  }

  String trFromSection(String section, [Map args = const {}]) {
    return '${this.friendlyName}||$section.${this.name}'.tr(args: args);
  }

  String get friendlyName {
    return this.name.toSnakeCase().split('_').map((e) => e.capitalize()).join(' ');
  }
}
