extension InvitationStatusTypeUtils on InvitationStatusType {
  InvitationStatusType get next {
    final index = (this.index + 1) % InvitationStatusType.values.length;
    return InvitationStatusType.values[index];
  }

  InvitationStatusType get previous {
    final index = (this.index - 1 + InvitationStatusType.values.length) %
        InvitationStatusType.values.length;
    return InvitationStatusType.values[index];
  }

  String trFromSection(String section, [Map args = const {}]) {
    return '${this.friendlyName}||$section.${this.name}'.tr(args: args);
  }

  String get friendlyName {
    return this
        .name
        .toSnakeCase()
        .split('_')
        .map((e) => e.capitalize())
        .join(' ');
  }
}
